from Crypto.Util.number import getPrime, inverse, bytes_to_long
from hashlib import sha256


def keygen(sz):
    p = getPrime(sz // 2)
    q = getPrime(sz // 2)
    n = p * q
    e = 65537
    dp = inverse(e, p - 1)
    dq = inverse(e, q - 1)
    return (n, e), (p, q, dp, dq)


def encrypt(pk, m):
    n, e = pk
    return pow(m, e, n)


def sign(sk, m):
    p, q, dp, dq = sk
    n = p * q
    sp = pow(m, dp, p)
    sq = pow(m, dp, q)
    u = inverse(q, p)
    s = sq + ((sp - sq) * u % p) * q
    return s


flag = open("flag.txt", "rb").read().strip()
pk, sk = keygen(2048)
m = bytes_to_long(flag)
h = bytes_to_long(sha256(flag).digest())
c = encrypt(pk, m)
s = sign(sk, h)
print(f"n = {pk[0]}")
print(f"e = {pk[1]}")
print(f"c = {c}")
print(f"s = {s}")

